import React, { useEffect, useState } from 'react'
import ListItem from './ListItem'

function App() {

const [ counter , setCounter] = useState(0)
const [ colorName , setColorName] = useState("red")
const [ initialColor  , setInitialColor] = useState(false)

useEffect(()=>{
  console.log("initialColor",initialColor)
  if(initialColor)
    setColorName("green")
  else
  setColorName("red")
},[initialColor])


function onclickIncrement(){
  setCounter(counter+1)
}

function onclickDecrement(){
  setCounter(counter-1)
}
function onclickRandom(){
  let x = Math.floor(Math.random()*10)
  setCounter(x)
}

const listAct = ["Wake up at 7", "Go for a walk", "Have Breakfast"]

  return (
    <div className="App">
      <p>{counter}</p>
      <button onClick={onclickIncrement}>Increment</button>
      <button onClick={onclickDecrement}>Decrement</button>
      <button onClick={onclickRandom}>Random Number</button>
      <div style={{height:'100px', width:'100px', background:`${colorName}`}}></div>
      <button onClick={()=>setInitialColor(!initialColor)}>Color Change</button>
      <div>
        <h1>List Of Activities</h1>
        {listAct.map((item) =>{
          return(<ListItem activity={item} />)
        })}
      </div>
    </div>
  );
}

export default App;
